import React from 'react'
import {useState} from 'react'

const SubmitForm=(props)=>{
    const[description,setDescription]=useState("");
    
    const[deadline,setDeadline]=useState("");

    const[completed,setCompleted]=useState("");

    const[id,setId]=useState("");

    const[editMode,setEditMode]=useState("");
    

    const onChangeName=(e)=>{
        setDescription(e.target.value);
       console.log("description",description);
   }
   
  const onChangeStartTime=(e)=>{
    setDeadline(e.target.value);
    console.log("deadline",deadline);
  }

  let count = 0;

  const UpdateForm=()=>{
      count = count +1;
      console.log(count)
    props.setShifts(prev => prev.concat({description: description,deadline:deadline,completed:completed,id:count,editMode:editMode}))
  }


  
   return (
       <div>
           Todo description<input type="text" value={description} onChange={onChangeName}/>
           <br></br>
           deadline<input type="date" value={deadline} onChange={onChangeStartTime}/>
           <br></br>
           <button onClick={UpdateForm}>Add todo</button>
       </div>
   )
}
export default SubmitForm;
