import React from 'react'

const CheckBox=(props)=>{
    
  const selectCheckBox=()=>{
    console.log('selectCheckBox');
    console.log(props.shift.completed)
    props.shift.completed = !props.shift.completed;
    props.setShifts(prev => prev.filter((item) => item))
  }

   return (
            <label style={{ textDecoration: props.shift.completed ? "line-through" : null, display: "none" }} >
              {props.shift.description} &nbsp; | &nbsp;
              {props.shift.deadline}
              &nbsp;
              <input type="text" style={{display: "block"}} value={props.shift.description} 
                     onChange={(e) => setEditedDescription(e.target.value)}></input>
              <input type="checkbox" onClick={(e) => {
                                selectCheckBox(e);
                            }} />
             </label>
            
   )
}
export default CheckBox;
