import React,{useEffect,useState,useCallback} from "react";

const DataFetching=(props)=>{
    const[cats,setCats]=useState([])
    const[isLoading,setIsLoading]=useState(true)
    const apiUrl='https://aws.random.cat/meow';

    const AddCat=useCallback(()=>{
        setIsLoading=true
        fetch(apiUrl)
        .then(res=>res.json())
        .then(result=>{
            const {file}=result
            setCats((lastState=>{
                return(
                    lastState.concat(file)
                )
            }))
            
            //const nextCats=cats.concat(file)
            //setCats(nextCats)
            //console.log("see states",cats,nextCats)
            //console.log("file",file)
        })
       
        console.log("i mounted");
    },[])
    

    useEffect(()=>{
        AddCat()
    },[AddCat])


    return(
        <div>
            {
                isLoading && (
                    <div>...loading</div>
                )
            }
            <button onClick={AddCat}>add cat</button>
            <p>i want to fetch cat images</p>
            {
               cats.map(cat=>{
                    return(
                        <img src={cat} alt="" height="200"/>
                    )
                })
            }
            
        </div>
    )
}
export default DataFetching;
